import { useState } from 'react';
import './App.css';

function App() {

  const [isBackgroundLight, setIsBackgroundLight] = useState(true)

  const onChangeBackground = () => {
    setIsBackgroundLight(prev => !prev)
  }

  console.log(isBackgroundLight)

  return (
    <div className={isBackgroundLight ? 'app light' : 'app black'}>
      <h1>hello</h1>
      <button onClick={onChangeBackground}>Click</button>
    </div>
  );
}

export default App;
