import React from 'react'
import './Theme.css'
import sun_icon from '../../assets/icons/sun_icon.svg'
import moon_icon from '../../assets/icons/moon_icon.svg'

export default function Theme({isThemeLight, switchTheme}) {
  return (
    <div className='switch-theme'>
        <img src={isThemeLight ? sun_icon : moon_icon} onClick={switchTheme} />
    </div>
  )
}
