import { useState } from 'react';
import './App.css';
import sun_icon from '../src/assets/icons/sun_icon.svg'
import moon_icon from '../src/assets/icons/moon_icon.svg'

function App() {

  const [isThemeLight, setIsThemeLight] = useState(true)

  const onChangeBackground = () => {
    setIsBackgroundLight(prev => !prev)
  }

  console.log(isBackgroundLight)

  return (
    <div className={isBackgroundLight ? 'app light' : 'app black'}>
      <h1>hello</h1>
      <img src={isBackgroundLight ? sun_icon : moon_icon} onClick={onChangeBackground} />
    </div>
  );
}

export default App;
